import React from "react";
import { makeStyles } from "@material-ui/core/styles";

// Importe o arquivo CSS da fonte de ícones ou caracteres aqui.

const useStyles = makeStyles((theme) => ({
  icon: {
    // Use a classe da fonte de ícones ou caracteres aqui.
    fontFamily: "FontAwesome", // Substitua pelo nome da fonte que você importou.
    fontSize: "24px", // Tamanho da letra.
    "&:hover": {
        color: theme.palette.primary.main, // Cor ao passar o mouse.
    },
    transition: "all 0.5s ease",
  },
}));

export const CustomLetter = () => {
    const classes = useStyles();

    return (
      <span className={classes.icon}>
        {/* Use o código da letra que você deseja mostrar */}
        &#xf105; {/* Exemplo: exibindo o ícone "" da FontAwesome */}
      </span>
    );
};
